INSERT INTO `categories` VALUES (5,'Laptop học tập, văn phòng','Laptop có cấu hình danh cho dân văn phòng, sinh viên và học sinh','product','2025-03-27 23:33:20','2025-05-22 00:00:00',1),(6,'Laptop gamming','Laptop có cấu hình cao dùng cho chơi game nặng','product','2025-03-27 23:33:20','2025-04-02 00:00:00',1),(7,'Laptop mỏng nhẹ','Dòng máy tính xách tay được thiết kế với trọng lượng nhẹ và kích thước mỏng, nhằm tối ưu tính di động cho người dùng thường xuyên phải mang theo máy để làm việc, học tập hoặc di chuyển.','product','2025-03-27 23:33:20','2025-05-21 00:00:00',1),(8,'Laptop AI','Laptop AI là loại máy tính xách tay được tích hợp hoặc hỗ trợ công nghệ trí tuệ nhân tạo, dùng để phát triển mô hình AI hoặc khai thác các tính năng AI như trợ lý ảo, xử lý ảnh, dịch ngôn ngữ, tối ưu hiệu suất và trải nghiệm người dùng.','product','2025-03-27 23:33:20','2025-05-19 00:00:00',1),(9,'Laptop đồ họa – kỹ thuật','Hiệu năng cao, chuyên xử lý đồ họa 2D, 3D, dựng phim, kỹ thuật.\nGPU chuyên dụng (NVIDIA Quadro, AMD Radeon Pro).\nMàn hình độ phân giải cao, độ chính xác màu tốt.','product','2025-03-27 23:33:20','2025-03-28 00:00:00',1),(10,'Laptop cao cấp','Thiết kế mỏng nhẹ, sang trọng, pin tốt.\nHiệu năng đủ mạnh cho công việc văn phòng, thuyết trình, hội họp.\nBảo mật cao hơn (vd: vân tay, TPM, camera IR).','product','2025-03-27 23:33:20','2025-04-27 00:00:00',1),(11,'Tin tức công nghệ','Các tin tức xoay quay laptop','news','2025-03-28 00:03:04','2025-03-28 00:00:00',1),(13,'Bảo mật và an toàn thông tin','Đây là tin loại tin tức về các cách thức bản mật cho máy tính','news','2025-03-28 15:56:16','2025-03-28 00:00:00',1),(16,'Phần mềm & Ứng dụng','Tin tức về các phần mềm, ứng dụng, hệ điều hành (Windows, Linux, Android...).','news','2025-05-07 12:00:19','2025-05-07 12:00:19',1),(17,'Phần cứng & Thiết bị','Máy tính, điện thoại, linh kiện, thiết bị IoT, thiết bị mạng.','news','2025-05-07 12:00:19','2025-05-07 12:00:19',1),(18,'Trí tuệ nhân tạo (AI) & Machine Learning','Xu hướng, ứng dụng AI, ChatGPT, deep learning, đạo đức AI.','news','2025-05-07 12:00:19','2025-05-07 12:00:19',1);
