/*!50003 CREATE*/ /*!50017 */ /*!50003 TRIGGER `trg_after_review_update` AFTER UPDATE ON `reviews` FOR EACH ROW BEGIN
    IF OLD.moderation_status <> NEW.moderation_status THEN
        UPDATE products 
        SET avg_rating = (
            SELECT AVG(rating) 
            FROM reviews 
            WHERE product_id = NEW.product_id 
              AND moderation_status = 'approved'
        )
        WHERE product_id = NEW.product_id;
    END IF;
END */;;
DELIMITER ;
