/*!50003 CREATE*/ /*!50017 */ /*!50003 TRIGGER `before_review_delete_cleanup_images` BEFORE DELETE ON `reviews` FOR EACH ROW BEGIN

    

    DELETE FROM images

    WHERE images.review_id = OLD.review_id;



END */;;
DELIMITER ;
