/*!50003 CREATE*/ /*!50017 */ /*!50003 TRIGGER `trg_images_before_delete_log_url` BEFORE DELETE ON `images` FOR EACH ROW BEGIN

    DECLARE v_dir_name VARCHAR(100); 



    

    IF OLD.image_url IS NOT NULL AND OLD.image_url != '' THEN



        

        IF OLD.review_id IS NOT NULL THEN

            SET v_dir_name = 'reviews';

        ELSEIF OLD.product_id IS NOT NULL THEN

            SET v_dir_name = 'products';

        ELSE

            SET v_dir_name = NULL; 

        END IF;



        

        INSERT INTO removed_images (image_url, dir_name, deleted_at)

        VALUES (OLD.image_url, v_dir_name, NOW()); 



    END IF;

END */;;
