/*!50003 CREATE*/ /*!50017 */ /*!50003 TRIGGER `trg_update_stock_quantity_on_order_status_change` AFTER UPDATE ON `orders` FOR EACH ROW BEGIN

    DECLARE v_export_status VARCHAR(255) DEFAULT 'shipping'; 
    DECLARE v_delivered_status VARCHAR(255) DEFAULT 'delivered';
    DECLARE v_cancelled_status VARCHAR(255) default 'cancelled';       


    DECLARE done INT DEFAULT FALSE;

    DECLARE v_product_id INT;

    DECLARE v_quantity INT;

    DECLARE cur_items CURSOR FOR

        SELECT product_id, quantity FROM order_items WHERE order_id = NEW.order_id;

    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
    IF new.status = v_export_status AND OLD.status <> v_export_status THEN
        OPEN cur_items;
        read_loop_complete: LOOP

            FETCH cur_items INTO v_product_id, v_quantity;

            IF done THEN

                LEAVE read_loop_complete;

            END IF;

            UPDATE products

            SET stock_quantity = GREATEST(0, stock_quantity - v_quantity),

                reserved_quantity = GREATEST(0, reserved_quantity - v_quantity)

            WHERE product_id = v_product_id;

        END LOOP;

        CLOSE cur_items;

    ELSEIF NEW.status = v_cancelled_status and OLD.status <> v_cancelled_status THEN
       IF OLD.status <> v_export_status and old.status <> v_delivered_status THEN

            SET done = FALSE;

            OPEN cur_items;

            read_loop_cancel: LOOP

                FETCH cur_items INTO v_product_id, v_quantity;

                IF done THEN

                    LEAVE read_loop_cancel;

                END IF;



                UPDATE products

                SET reserved_quantity = GREATEST(0, reserved_quantity - v_quantity)

                WHERE product_id = v_product_id;

            END LOOP;

            CLOSE cur_items;

       ELSE
            SET done = FALSE; 

            OPEN cur_items;

            read_loop_cancel_after_complete: LOOP

                FETCH cur_items INTO v_product_id, v_quantity;

                IF done THEN

                    LEAVE read_loop_cancel_after_complete;

                END IF;

                UPDATE products

                SET stock_quantity = stock_quantity + v_quantity

                WHERE product_id = v_product_id;

            END LOOP;

            CLOSE cur_items;

        END IF;

    END IF;

END */;;
