/*!50003 CREATE*/ /*!50017 */ /*!50003 TRIGGER `trg_users_after_update_log_avatar` AFTER UPDATE ON `users` FOR EACH ROW BEGIN

    

    IF OLD.avatar_url IS NOT NULL AND OLD.avatar_url != '' AND OLD.avatar_url <> NEW.avatar_url THEN

        INSERT INTO removed_images (image_url, dir_name)

        VALUES (OLD.avatar_url, "users");

    END IF;

END */;;
