/*!50003 CREATE*/ /*!50017 */ /*!50003 TRIGGER `after_insert_import_detail` AFTER INSERT ON `import_details` FOR EACH ROW BEGIN

    UPDATE products

    SET stock_quantity = stock_quantity + NEW.quantity

    WHERE product_id = NEW.product_id;

END */;;
