/*!50003 CREATE*/ /*!50017 */ /*!50003 TRIGGER `trg_brands_after_update_logo_url` AFTER UPDATE ON `brands` FOR EACH ROW BEGIN

    

    IF OLD.logo_url IS NOT NULL AND OLD.logo_url != '' AND OLD.logo_url <> NEW.logo_url THEN

        INSERT INTO removed_images (image_url, dir_name)

        VALUES (OLD.logo_url, "brands");

    END IF;

END */;;
